import React from "react";
import PageHeader from "../pageHeader";
const BasicPackages = () => {
  const packages = [
    {
      name: "Basic Package",
      price: "$20",
      features: ["Basic Marker", "50 Paintballs"],
    },
    {
      name: "Standard Package",
      price: "$35",
      features: ["Upgraded Marker", "100 Paintballs", "Safety Gear"],
    },
    {
      name: "Premium Package",
      price: "$50",
      features: ["Premium Marker", "200 Paintballs", "Safety Gear", "Snacks"],
    },
  ];

  return (
    <div className="row">
        {packages.map((packageKind, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card h-100">
              <div className="card-body">
                <h5 className="card-title">{packageKind.name}</h5>
                <p className="card-text">Price: {packageKind.price}</p>
                <ul className="list-group list-group-flush">
                  {packageKind.features.map((feature, i) => (
                    <li key={i} className="list-group-item">
                      {feature}
                    </li>
                  ))}
                </ul>
              </div>
              <div className="card-footer text-center">
                <button className="btn btn-primary">Book Now</button>
              </div>
            </div>
          </div>
        ))}
      </div>
  );
};

export default BasicPackages;
