import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/auth.context";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const SentEmail = () => {
  const { logIn, user, refreshUser } = useAuth();
  const TOKEN_KEY = "token";
  const [errorApiRequest, setErrorApiRequest] = useState("");
  const [timer, setTimer] = useState(300); // Initial timer value in seconds

  const [verificationCode, setVerificationCode] = useState("");
  const navigate = useNavigate();

  const handleVerificationCodeChange = (e) => {
    const inputCode = e.target.value;
    // You may want to add additional validation here
    setVerificationCode(inputCode);
  };
  const startTimer = () => {
    // Set a timer to decrement the timer value every second
    const intervalId = setInterval(() => {
      setTimer((prevTimer) => prevTimer - 1);

      if (timer === 0) {
        return
        // Clear the interval when the timer reaches 0
        setTimer(null);
        clearInterval(intervalId);
        return;
      }
    }, 1000);
  };
  useEffect(() => {
    startTimer();
    // Reset the timer and cleanup when the component unmounts
    return () => {
      setTimer(10);
    };
  }, []);

  // useEffect(() => {
  //   // When the timer reaches 0, perform any cleanup or reset necessary
  //   if (timer === 0) {
  //     setVerificationCode(null);
  //     // Reset the timer and perform any other necessary actions
  //     setTimer(300);
  //   }
  // }, [timer]);
  const handleVerificationCodeSubmit = async () => {
    // Check if the entered code is valid (you may want to compare it with a server-generated code)
    const isValidCode = verificationCode.length === 4; // Example: Check if the code has 4 digits

    if (isValidCode) {
      const response = await fetch(
        "http://localhost:3003/reset-password/sent-email",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ verificationCode }),
        }
      );

      const { user, userToken, error } = await response.json();
      localStorage.setItem(TOKEN_KEY, userToken);
      localStorage.getItem(TOKEN_KEY);
      refreshUser();
      setErrorApiRequest(error);
      toast.success(`verification Code passed correctly`, {
        autoClose: 2000,
        style: {
          background: "black",
          color: "white",
          borderRadius: "8px",
          boxShadow: "0 0 10px rgba(0, 0, 0, 0.5)",
        },
      });

      // Navigate to the "Change Password" component
      navigate(`/users/change-password/via-email-code/${user._id}`, {
        state: { user },
      });
    } else {
      // Handle invalid code (show an error message or perform other actions)
      console.error("Invalid verification code");
    }
  };

  return (
    <div>
      <h2>Enter Verification Code</h2>
      <p>Please enter the 4-digit verification code sent to your email.</p>

      <input
        type="text"
        value={verificationCode}
        onChange={handleVerificationCodeChange}
        maxLength={4}
      />

      <button onClick={handleVerificationCodeSubmit}>
        Submit Verification Code
      </button>
      <div>
        <p className="text-danger">{errorApiRequest}</p>
      </div>
      <div>
        <p>
          Time remaining: {Math.floor(timer / 60)}:{timer % 60}
        </p>
      </div>
    </div>
  );
};

export default SentEmail;
