import "./App.css";
import Navbar from "./components/navbar";
import { Route, Routes } from "react-router-dom";
import Footer from "./components/common/footer";
import CreateActivityCard from "./components/createActivityCard";
import SignOut from "./components/signOut";
import MyActivityCards from "./components/myActivityCards";
import ProtectedRoute from "./components/protectedRoute";
import About from "./components/about";
import EditActivityCard from "./components/editActivityCard";
import Home from "./components/home";
import SignIn from "./components/signIn";
import SignUp from "./components/signUp";
import SignUpBiz from "./components/signUpBiz";
import DeleteActivityCard from "./components/common/deleteActivityCard";
import PaymentForm from "./components/common/payment";
import Calendar from "./components/calendar";
import EmailForm from "./components/formToGmail";
import ChangePassword from "./components/changePassword";
import ResetPassword from "./components/resetPassword";
import SentSms from "./components/";

import { date } from "joi";
import { loadStripe } from "@stripe/stripe-js";
import {
  CardElement,
  Elements,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";
import { AppProvider } from "./context/card.context";

const stripePromise = loadStripe(
  "pk_test_51OXTK9FzIkHLxdyfqYLsI9aG4k28P6nhqV0o42t2vVBgD6j0UUBrinpOLAAS4l5tuJ3X9spREb83JyMUIyByhYew00dkMjUQlN"
);
function App() {
  return (
    <div className="App">
      <header className="mb-5">
        <Navbar />
      </header>
      <main>
        <AppProvider>
          <Routes>
            <Route path="/" element={<Home />}></Route>
            <Route path="/calendar" element={<Calendar />}></Route>
            <Route path="/about" element={<About />}></Route>
            <Route
              path="/cards/create-activity-card"
              element={<CreateActivityCard />}
            ></Route>
            <Route
              path="/cards/payment/:id"
              element={
                <Elements stripe={stripePromise}>
                  <PaymentForm />
                </Elements>
              }
            ></Route>
            <Route
              path="cards/my-activity-cards/:id"
              element={
                <ProtectedRoute onlyBiz>
                  <MyActivityCards />
                </ProtectedRoute>
              }
            ></Route>
            <Route
              path="cards/edit-activity-cards/:id" //should add query param :id  ?
              element={
                <ProtectedRoute id>
                  <EditActivityCard />
                </ProtectedRoute>
              }
            ></Route>
            <Route
              path="/cards/delete-activity-cards/:id"
              element={
                <ProtectedRoute>
                  <DeleteActivityCard />
                </ProtectedRoute>
              }
            ></Route>
            <Route path="sign-in" element={<SignIn />}></Route>
            <Route path="send-email" element={<EmailForm />}></Route>
            <Route path="users/change-password/:id" element={ <ProtectedRoute id><ChangePassword /></ProtectedRoute>}></Route>
            <Route path="sign-up" element={<SignUp />}></Route>
            <Route path="sign-out" element={<SignOut />}></Route>
            <Route path="sign-up-biz" element={<SignUpBiz />}></Route>{" "}
            <Route path="reset-password" element={<ResetPassword />}></Route>{" "}
          </Routes>
        </AppProvider>
      </main>
      <footer>
        <Footer />
      </footer>
    </div>
  );
}

export default App;
