import React from "react";

const PaintballPage = () => {
  const paintballPackages = [
    { name: "Basic Package", price: "$20", features: ["Basic Marker", "50 Paintballs"] },
    { name: "Standard Package", price: "$35", features: ["Upgraded Marker", "100 Paintballs", "Safety Gear"] },
    { name: "Premium Package", price: "$50", features: ["Premium Marker", "200 Paintballs", "Safety Gear", "Snacks"] },
  ];

  return (
    <div className="container mt-5">
    <h1 className="text-center mb-4">Paintball Packages</h1>

    <div className="row">
      {paintballPackages.map((paintballPackage, index) => (
        <div key={index} className="col-md-4 mb-4">
          {/* ... (your existing card code) */}
        </div>
      ))}
    </div>

    <div className="mt-5">
      <h2>Welcome to Paintball Adventures!</h2>
      <p>
        Immerse yourself in the excitement of paintball with our thrilling packages. Whether you're a beginner or a seasoned player, we have the perfect experience for you.
      </p>
      <p>
        Our paintball packages include top-notch markers, safety gear, and plenty of paintballs to keep the action going. Book your adventure today and create lasting memories with friends and family.
      </p>
      <p>
        If you have any questions or special requests, feel free to contact our team. We're here to ensure you have an unforgettable paintball experience!
      </p>
    </div>
  </div>
);
};

export default PaintballPage;
