import React, { useEffect, useState } from "react";

const FooterManu = () => {
  const [showMenu, setShowMenu] = useState(false); // State to toggle menu visibility

  // useEffect to handle menu animation
  useEffect(() => {
    const timeout = setTimeout(() => {
      setShowMenu(true); // Set showMenu to true after 2 seconds
    }, 2000);
    return () => clearTimeout(timeout); // Clean up to avoid memory leaks
  }, []);

  return (
    <footer className="footer">
      <div className="container-fluid">
        <img
        
          src="https://cdn.pixabay.com/photo/2015/12/07/10/24/go-kart-1080492_1280.jpg"
          alt="Footer Background"
          className="footer-image"
        />
        {showMenu && (
          <>
            <ul className="footer-menu">
              <li>Menu 1</li>
              <li>Menu 2</li>
              <li>Menu 3</li>
            </ul>
            <ul className="footer-menu">
              <li>Menu 4</li>
              <li>Menu 5</li>
              <li>Menu 6</li>
            </ul>
            <ul className="footer-menu">
              <li>Menu 7</li>
              <li>Menu 8</li>
              <li>Menu 9</li>
            </ul>
          </>
        )}
      </div>
    </footer>
  );
};

export default FooterManu;
