import React from "react";

const PaintballPage = () => {
  const packages = [
    { name: "Basic Package", price: "$20", features: ["Basic Marker", "50 Paintballs"] },
    { name: "Standard Package", price: "$35", features: ["Upgraded Marker", "100 Paintballs", "Safety Gear"] },
    { name: "Premium Package", price: "$50", features: ["Premium Marker", "200 Paintballs", "Safety Gear", "Snacks"] },
  ];

  return (
    <div className="container mt-5">
    <h1 className="text-center mb-4">Paintball Packages</h1>

    <div className="row">
      {paintballPackages.map((paintballPackage, index) => (
        <div key={index} className="col-md-4 mb-4">
          <div className="card h-100">
            <div className="card-body">
              <h5 className="card-title">{paintballPackage.name}</h5>
              <p className="card-text">Price: {paintballPackage.price}</p>
              <ul className="list-group list-group-flush">
                {paintballPackage.features.map((feature, i) => (
                  <li key={i} className="list-group-item">{feature}</li>
                ))}
              </ul>
              <p className="mt-3">Experience the thrill of paintball with our {paintballPackage.name}! This package includes:</p>
              <ul>
                {paintballPackage.features.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
            </div>
            <div className="card-footer text-center">
              <button className="btn btn-primary">Book Now</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);
  
};

export default PaintballPage;
