  // now i get props as undefined
  const { user } = useAuth();
  console.log(id);
  console.log(user._id);
  console.log(myTasks);
  if (
    !user ||
    user._id !== id ||
    myTasks[0].user_id !== user._id /*(onlyBiz && !user.biz)*/
  ) {
    return; //<Navigate to="sign-in" />;
