    console.log("now it got the data from server");
  }
  if (
    !user ||loading
    
    //  ||
    // !myTasks ||
    // myTasks.length === 0 ||
    // user._id !== id ||
    //myTasks[0].user_id !== user._id /*(onlyBiz && !user.biz)*/
  ) {
    return null;
  }
  {
    return children;
    //return <Navigate to="/" />;
  }
};
export default ProtectedRoute;
