import React, { useState } from "react";
import axios from "axios";
const EmailForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setFormData({
      name: "",
      email: "",
      message: "",
    });

    axios
      .post("/send-email", formData)
      .then((response) => {
        console.log("Email sent successfully!");
      })

      .catch((error) => {
        console.error("Error sending email:", error);
      });
  };

  return (

    <div className="h-100 d-flex justify-content-center align-items-center">
  <h3 className="badge bg-primary align-self-start flex-fill "> <form style={{ maxWidth: "200px", margin: "auto" }} onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          className="form-control"
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          className="form-control"
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label htmlFor="message">Message:</label>
        <textarea
          className="form-control"
          id="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
          required
        />
      </div>
      <button className="btn btn-success" type="submit">
        Send Email
      </button>
      <p>
        Send us an email and the manager will insert your activity in the
        calendar and update with a return email{" "}
      </p>
    </form></h3>
  <h3 className="badge bg-primary">Flex item 2</h3>
  <h3 className="badge bg-primary">Flex item 3</h3>
</div>
   
  );
};

export default EmailForm;
