import { useEffect, useState } from "react";
import PageHeader from "./common/pageHeader";
import CreateActivityCard from "./createActivityCard";
import { Link } from "react-router-dom";
import SendUsMailComp from "./common/sendUsMailComp";

const Home = () => {
  const [showCallUs, setShowCallUs] = useState(false);
  const [visitCount, setVisitCount] = useState(0);

  useEffect(() => {
    const storedVisitCount = localStorage.getItem("visitCount");
    const parsedVisitCount = parseInt(storedVisitCount);

    if (!isNaN(parsedVisitCount)) {
      setVisitCount(parsedVisitCount);
    }

    setVisitCount(visitCount + 1);
    localStorage.setItem("visitCount", visitCount + 1);

    if ((visitCount + 1) % 5 === 0) {
      setShowCallUs(true);
    }
  }, []);

  const handleDeleteClick = () => {
    setShowCallUs(false);
  };

  return (
    <>
      {/* Call Us card */}
      {showCallUs && (
        <div
          className="call-us-card z-3 position-absolute"
          style={{ width: "30%" }}
        >
          <button className="delete-button" onClick={handleDeleteClick}>
            <i classNames="bi bi-x-lg">X</i>
          </button>
          <p>Call Us: 123-456-7890</p>
          <SendUsMailComp />
        </div>
      )}

      {/* Main content */}
      <div className="container">
        <div className="row align-items-center">
          {/* Carousel */}
          <div
            id="carouselExampleIndicators"
            className="carousel slide mb-4 col-12"
            data-bs-ride="true"
          >
            <div className="carousel-inner">
              {/* First carousel item */}
              <div className="carousel-item active">
                <img
                  id="carousel-img"
                  src="https://cdn.pixabay.com/photo/2017/07/09/03/37/paintball-2486109_960_720.jpg"
                  className="d-block w-100"
                  alt="..."
                />
                <div className="carousel-caption d-none d-md-block">
                  <Link
                    to={"/paintball-page"}
                    className="btn btn-link text-decoration-none  p-2 rounded"
                  >
                    <p id="carousel-p-tag">paintball</p>
                  </Link>
                  <p className="text-decoration-none p-2 rounded">
                    paintball prices and terms
                  </p>
                </div>
                {/* Paragraph inside carousel item */}
                <div className="carousel-caption d-none d-md-block">
                  <p>
                    Hello and welcome to the home page of the Paintball Israel
                    website. In order to get more details, watch a video of
                    activities and proceed to determine the date of the
                    activity, you must sign in to the website. in order to
                    create activities, sign in as admin
                  </p>
                </div>
              </div>

              {/* Other carousel items */}
            </div>
            {/* Carousel controls */}
            <button
              className="carousel-control-prev"
              type="button"
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide="prev"
            >
              <span
                className="carousel-control-prev-icon"
                aria-hidden="true"
              ></span>
              <span className="visually-hidden">Previous</span>
            </button>
            <button
              className="carousel-control-next"
              type="button"
              data-bs-target="#carouselExampleIndicators"
              data-bs-slide="next"
            >
              <span
                className="carousel-control-next-icon"
                aria-hidden="true"
              ></span>
              <span className="visually-hidden">Next</span>
            </button>
          </div>
        </div>

        {/* Create Activity Card */}
        <div className="col-12">
          <CreateActivityCard />
        </div>
      </div>
    </>
  );
};

export default Home;
